const requestHandler = require("ards-client/tags/handler");
class Meme {
    random() {
        let subreddits = ["deepfriedmemes", "surrealmemes", "bonehurtingjuice", "dankmemes", "memes"];
        return requestHandler.makeRequest("reddit", subreddits[Math.floor(Math.random() *subreddits.length)]);
    }
}
module.exports = new Meme();